mapper.select:
select t.*,t.PROC_INST_ID procInstId,t.C_ACTIVITYID cActivityid from WF_TASK t where proc_inst_id = #{instid}

    and c_activityid in (select activityid from WF_RE_NODEATTR where NODEKEY = #{nodekey} and ATTRVALUE =
    #{attrvalue})

select * from (
select rownum rn,s.* from (
select * from GET_XCKC_FQ t where 1=1

    and t.rwzt = #{rwzt}


    and t.xmjl = #{xmjl}


    and t.xmbh LIKE CONCAT('%',CONCAT(#{xmbh},'%'))


    and t.dwmc LIKE CONCAT('%',CONCAT(#{dwmc},'%'))


    and t.lxr LIKE CONCAT('%',CONCAT(#{lxr},'%'))


    and to_date(t.slrq,'yyyy-MM-dd') >= to_date(#{kssj},'yyyy-MM-dd')


    and to_date(t.slrq,'yyyy-MM-dd') < to_date(#{jssj},'yyyy-MM-dd') +1

order by slrq desc ) s
where rownum  <=  #{endRow}
) where rn  >=  #{startRow}

select * from
(select rownum rn,s.* from( select * from GET_LXLB t where 1=1 and t.sflx = #{sflx}

    and t.xmjl = #{xmjl}


    and t.sqbh LIKE CONCAT('%',CONCAT(#{sqbh}, '%'))


    and t.dwmc LIKE CONCAT('%',CONCAT(#{dwmc},'%'))


    and t.lxr LIKE CONCAT('%',CONCAT(#{lxr},'%'))


    and to_date(t.slrq,'yyyy-MM-dd hh24:mi:ss')   >=   to_date(#{kssj},'yyyy-MM-dd')


    and to_date(t.slrq,'yyyy-MM-dd hh24:mi:ss')   <   to_date(#{jssj},'yyyy-MM-dd') + 1

order by slrq desc ) s
where rownum  <=  #{endRow}
) where rn  >=  #{startRow}

select * from
(select rownum rn,s.* from( select t.*,to_char(t.LXSJ,'yyyy-mm-dd') lxsj_str from XM_INSTANCE t where t.pxmid is
null

    and t.xmbh LIKE CONCAT('%',CONCAT(#{xmbh}, '%'))


    and t.xmmc LIKE CONCAT('%',CONCAT(#{xmmc},'%'))


    and LXSJ   >=   to_date(#{kssj},'yyyy-MM-dd')


    and LXSJ  <   to_date(#{jssj},'yyyy-MM-dd') + 1

order by LXSJ desc ) s
where rownum  <=  #{endRow}
) where rn  >=  #{startRow}

SELECT codename,codevalue from XM_CODE WHERE PARENTID = (
SELECT ID from XM_CODE WHERE CODENAME = #{codename}
)

    and TYPE = #{type}

order by sort

select t.id,t.name||'提供资料' codename,t.parentid,count(a.id) count from XTGL_FJZLGL t
	left join ${tablename} a on t.id=a.fjlx_yj and a.sc_flag=0 and a.${fieldname} = #{sqdid}
	where t.id= #{fjlx}
	group by t.id,t.name,t.parentid
union all

select  t.id,t.name codename,t.parentid,count(b.id) count from XTGL_FJZLGL t
	left join ${tablename} b on t.id = b.fjlx_ej and b.sc_flag=0 and b.${fieldname} = #{sqdid}
	where t.parentid = #{fjlx}
	group by t.id,t.name,t.parentid
